import React, { useState, useEffect } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import { Autocomplete } from '@material-ui/lab';
import { TextField } from '@material-ui/core'
import mentoring from '../../images/mentoring.jpg'
import './FindMentor.css';
import Viewmore from './viewmore';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
  search: {
    width: '70%',
    margin: 'auto',
    backgroundColor: 'white'
  },
  root: {
    paddingTop: '20vh',
    width: '70%',
    margin: 'auto',


  },
  //   background: {
  //     // backgroundImage: `url(${mentoring})`,
  //     // height: '85vh',
  //     // backgroundPosition: 'center',
  //     // backgroundSize: 'cover',
  //     // backgroundRepeat: 'no-repeat',
  //     // paddingBottom: 0,
  //     // marginBottom: 0,
  // }
});


const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}



const Mentor = (props) => {
  const classes = useStyles();

  const searchOptions = [
      { name: "Algebra 1" },
      { name: "Algebra 2" },
      { name: "Pre-Calculus" },
      { name: "Calculus I" },
      { name: "Calculus II" },
      { name: "Calculus III/Multivariable Calculus" },
      { name: "Linear Algebra" },
      { name: "Differential Equations" },
      { name: "Discrete Mathematics" },
      { name: "General Chemistry" },
      { name: "Organic Chemistry" },
      { name: "Analytical Chemistry" },
      { name: "Physical Chemistry" },
      { name: "Chem Lab Advise" },
      { name: "Java" },
      { name: "Python" },
      { name: "C/C++/C#" },
      { name: "Javascript/jQuery" },
      { name: "Web Development" },
      { name: "Backend Development" },
      { name: "Algorithms" },
      { name: "Data Structures" },
      { name: "Interview Prep" },
      { name: "General Biology" },
      { name: "Cellular Biology" },
      { name: "Anatomical Sciences" },
      { name: "Ecology" },
      { name: "Evolutionary Biology" },
      { name: "Molecular Biology" },
      { name: "Genetics" },
      { name: "Biochemistry" },
      { name: "Bio Physics" },
      { name: "Writing advise" },
      { name: "Shakespeare" },
      { name: "Poetry" },
      { name: "Nonfiction" },
      { name: "Fiction" },
      { name: "Novel" },
      { name: "Drama" },
      { name: "Spanish" },
      { name: "French" },
      { name: "German" },
      { name: "Japanese" },
      { name: "Mandarin Chinese" },
      { name: "Hindi" },
      { name: "English" }
    ]


    const [mentors, setMentors] = useState([]);

  const getMentors = async () => {
    try {
      const response = await fetch("http://localhost:5000/mentors");
      const jsonData = await response.json();
      console.log(jsonData);
      setMentors(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getMentors();
  }, []);


  return (
    <div className="background">
      <div className={classes.root}>
        <Autocomplete
          multiple
          options={searchOptions}
          getOptionLabel={(option) => option.name}
          className={classes.search}
          renderInput={(params) => (

            <TextField
              {...params}
              variant="filled"
              label="Find your mentor..."
            />

          )}
        />

      <TableContainer style={{marginTop: '30px'}} component={Paper}>
      <Table className={classes.table} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell align="left">Name</StyledTableCell>
            <StyledTableCell align="center">Email</StyledTableCell>
            <StyledTableCell align="right">More Info</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {mentors.map((mentor) => (
            <StyledTableRow >
              <StyledTableCell align="left">{mentor.fname} {mentor.lname}</StyledTableCell>
              <StyledTableCell align="center"> {mentor.email}</StyledTableCell>
              <StyledTableCell align="right"> <Viewmore id={mentor.mentor_id}/></StyledTableCell>
            </StyledTableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>

      </div>
    </div>
  );
}

export default Mentor;
